import React, { Fragment } from "react";
import styled from "styled-components";
class Form extends React.Component {
  render() {
    return (
      <StyledForm>
        <StyledInput type="text" name="first_name" placeholder={"First Name"} />
        <StyledInput type="text" name="last_name" placeholder={"Last Name"} />
        <StyledInput type="email" name="email" placeholder={"Email"} />
        <StyledInput type="password" name="password" placeholder={"Password"} />
        <StyledButton type={"submit"}>Claim your free Trial</StyledButton>
        <p>By clicking the button you are agreeing to our Terms and Services</p>
      </StyledForm>
    );
  }
}

const StyledForm = styled.form`
  display: grid;
  grid-template-rows: 1fr;
  place-items: center;
  max-width: 500px;
  background: white;
  padding: 20px;
  border-radius: 10px;
  input,
  button {
    max-width: 300px;
    min-width: 250px;
  }
`;
const StyledInput = styled.input`
  padding: 20px;
`;
const StyledButton = styled.button`
  padding: 20px;
`;
export default Form;
