import React, { Fragment } from "react";
import styled from "styled-components";
import StyledTerms from "./styles/StyledTerms";
class Form extends React.Component {
  state = {
    first_name: "",
    last_name: "",
    email: "",
    password: "",
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.clearForm();
  };
  clearForm = () =>
    this.setState({
      first_name: "",
      last_name: "",
      email: "",
      password: "",
    });
  saveToState = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    return (
      <StyledForm onSubmit={this.handleSubmit}>
        <label htmlFor={"first_name"}>
          <input
            type="text"
            name="first_name"
            required
            value={this.state.first_name}
            placeholder={"First Name"}
            onChange={this.saveToState}
          />
        </label>
        <label htmlFor={"htmlFor"}>
          <input
            type="text"
            name="last_name"
            required
            value={this.state.last_name}
            placeholder={"Last Name"}
            onChange={this.saveToState}
          />
        </label>
        <label htmlFor={"email"}>
          <input
            type="email"
            name="email"
            required
            value={this.state.email}
            placeholder={"Email"}
            onChange={this.saveToState}
          />
        </label>
        <label htmlFor={"password"}>
          <input
            type="password"
            name="password"
            required
            pattern="^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?!.*\s).*$"
            title="Please include at least 1 uppercase character, 1 lowercase character, and 1 number."
            autoComplete="suggested-password"
            value={this.state.password}
            placeholder={"Password"}
            onChange={this.saveToState}
          />
        </label>

        <button type={"submit"}>Claim your free Trial</button>
        <StyledTerms>
          By clicking the button you are agreeing to our{" "}
          <a>Terms and Services</a>
        </StyledTerms>
      </StyledForm>
    );
  }
}

const StyledForm = styled.form`
  display: grid;
  grid-template-rows: 1fr;
  grid-gap: 25px;
  place-items: center;
  width: 70%;
  min-width: 250px;
  margin: 0 auto;
  background: white;
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 6px rgba(0, 0, 0, 0.3);
  label {
    position: relative;
    width: 100%;
  }
  input {
    width: 100%;
    border: 2px solid lightgray;
    &:invalid {
      border: 2px solid ${(props) => props.theme.red};
    }
  }
  & + label::after {
    position: absolute;
    content: url("/icon-error.svg");
    display: inline-block;
    right: 5%;
    bottom: 0;
  }
  input:active,
  input:focus {
    border: 2px solid ${(props) => props.theme.grayishBlue};
  }
  button {
    width: 100%;
    text-transform: uppercase;
    background: ${(props) => props.theme.green};
    color: white;
    box-shadow: inset 0 -5px hsl(154, 59%, 41%);
    border: 1px solid transparent;
    cursor: pointer;
    :active {
      box-shadow: inset 0 -2px hsl(154, 59%, 41%);
      transform: translateY(2px);
    }
  }
  input,
  button {
    padding: 10px;
    border-radius: 5px;
  }
`;

export default Form;
