import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`

html{
  box-sizing: border-box;
  font-size: 10px;
  min-height: 100%;
  height: 100%;
}
*, *:before, *:after{
  box-sizing: inherit;
}
body{
  padding: 0;
  margin: 0;
  font-size: 1.6rem;
  line-height: 2;
  height: 100%;
  font-family: 'Poppins', sans-serif;
}
input, button{
  font-family: 'Poppins', sans-serif;
  outline: none;
}
`;
export default GlobalStyle;
