import React, { Fragment } from "react";
import styled from "styled-components";
import StyledTerms from "./styles/StyledTerms";
class Form extends React.Component {
  state = {
    first_name: "",
    last_name: "",
    email: "",
    password: "",
    error: [null, null, null, null],
  };
  handleSubmit = (e) => {
    e.preventDefault();
    this.clearForm();

    e.target.checkValidity();
    console.log(e.target.checkValidity());
  };
  clearForm = () =>
    this.setState({
      first_name: "",
      last_name: "",
      email: "",
      password: "",
      error: [null, null, null, null],
    });
  saveToState = (e) => {
    const error = this.state.error.splice(e.target.id, 1, "true");

    this.setState({ error });

    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    return (
      <StyledForm method="post" noValidate onSubmit={this.handleSubmit}>
        <div>
          <input
            type="text"
            name="first_name"
            required
            id="0"
            error={this.state.error[0]}
            value={this.state.first_name}
            placeholder="First Name"
            onChange={this.saveToState}
          />
          <div className={"error"}>First Name cannot be empty</div>
        </div>
        <div>
          <input
            type="text"
            name="last_name"
            required
            id="1"
            error={this.state.error[1]}
            value={this.state.last_name}
            placeholder="Last Name"
            onChange={this.saveToState}
          />
          <div className={"error"}>Last Name cannot be empty</div>
        </div>

        <div>
          <input
            type="email"
            name="email"
            required
            id="2"
            error={this.state.error[2]}
            value={this.state.email}
            placeholder="Email"
            onChange={this.saveToState}
          />
          <div className={"error"}>Looks like this is not an email</div>
        </div>
        <div>
          <input
            type="password"
            name="password"
            required
            id="3"
            error={this.state.error[3]}
            pattern="^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?!.*\s).*$"
            title="Please include at least 1 uppercase character, 1 lowercase character, and 1 number."
            autoComplete="suggested-password"
            value={this.state.password}
            placeholder={"Password"}
            onChange={this.saveToState}
          />
          <div className="error">Password cannot be empty</div>
        </div>

        <button type="submit">Claim your free Trial</button>
        <StyledTerms>
          By clicking the button you are agreeing to our{" "}
          <a>Terms and Services</a>
        </StyledTerms>
      </StyledForm>
    );
  }
}

const StyledForm = styled.form`
  display: grid;
  grid-template-rows: 1fr;
  grid-gap: 25px;
  place-items: center;
  width: 70%;
  min-width: 250px;
  margin: 0 auto;
  background: white;
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 6px rgba(0, 0, 0, 0.3);
  div {
    width: 100%;
    .error {
      font-size: 1rem;
      text-align: end;
      opacity: 0;
      color: ${(props) => props.theme.red};
    }
    input:invalid::placeholder {
      color: ${(props) => props.theme.red};
    }
    input:invalid {
      border: 2px solid ${(props) => props.theme.red};
    }
    input:invalid + .error {
      opacity: 1;
    }
  }

  input,
  button {
    padding: 10px;
    border-radius: 5px;
  }

  input:invalid {
    border: 2px solid ${(props) => props.theme.red};
    background: url("/icon-error.svg");
    background-size: 20px;
    background-repeat: no-repeat;
    background-position: 90%;
  }

  input {
    width: 100%;
    border: 2px solid lightgray;
  }

  input:active,
  input:focus {
    border: 2px solid ${(props) => props.theme.grayishBlue};
  }
  button {
    width: 100%;
    text-transform: uppercase;
    background: ${(props) => props.theme.green};
    color: white;
    box-shadow: inset 0 -5px hsl(154, 59%, 41%);
    border: 1px solid transparent;
    cursor: pointer;
    :active {
      box-shadow: inset 0 -2px hsl(154, 59%, 41%);
      transform: translateY(2px);
    }
  }
`;

export default Form;
